package com.roy.miscellaneous.leetcode.stage1.stage13;

import org.slf4j.LoggerFactory;

/**
 * @author guojun
 * @date 2020/7/22
 *
 *
 * 696. 计数二进制子串
 * 给定一个字符串 s，计算具有相同数量0和1的非空(连续)子字符串的数量，并且这些子字符串中的所有0和所有1都是组合在一起的。
 *
 * 重复出现的子串要计算它们出现的次数。
 *
 * 示例 1 :
 *
 * 输入: "00110011"
 * 输出: 6
 * 解释: 有6个子串具有相同数量的连续1和0：“0011”，“01”，“1100”，“10”，“0011” 和 “01”。
 *
 * 请注意，一些重复出现的子串要计算它们出现的次数。
 *
 * 另外，“00110011”不是有效的子串，因为所有的0（和1）没有组合在一起。
 * 示例 2 :
 *
 * 输入: "10101"
 * 输出: 4
 * 解释: 有4个子串：“10”，“01”，“10”，“01”，它们具有相同数量的连续1和0。
 * 注意：
 *
 * s.length 在1到50,000之间。
 * s 只包含“0”或“1”字符。
 *
 *
 */
public class TestSolution696 {
    private static final org.slf4j.Logger logger = LoggerFactory.getLogger(TestSolution696.class);


    /**
     *
     * s.length 在1到50,000之间。
     * s 只包含“0”或“1”字符。
     *
     */
    static class Solution {
        /**
         *
         *
         * 超出时间限制 todo
         * 思路：1. p=0,找第一个"不同的元素"并记录相同的个数num,记录下标 q
         *      2. 从q开始，找num个"不同的元素"，找不够跳出， p++
         *
         */
        public int countBinarySubstrings(String s) {
            int len = s.length();
            if (len == 1) {
                return 0;
            }
            char[] chars = s.toCharArray();
            int p;
            int q ;
            int ret = 0;

            for (p = 0; p < len ; p ++) {
                char pChar = chars[p];
                char qChar = (pChar == '1' ? '0' : '1') ;
                int appearTimes = 1;
                q = p +1;
                //find first diff
                while (q < len && chars[q] == pChar) {
                    appearTimes ++;
                    q ++;
                }
                // not find
                if (q == len) {
                    return ret;
                }
                //can find nums diffs
                int diffAppearTimes = 0;
                for (; q < len && diffAppearTimes < appearTimes ; q++) {
                    if (chars[q] != qChar ) {
                        break;
                    }
                    diffAppearTimes ++;
                }
                if (diffAppearTimes == appearTimes) {
                    ret ++;
                }
            }
            return ret;
        }

        public int countBinarySubstrings1(String s) {
            int len = s.length();
            if (len == 1) {
                return 0;
            }
            char[] chars = s.toCharArray();
            int p;
            int q ;
            int ret = 0;

            for (p = 0; p < len ; p ++) {
                char pChar = chars[p];
                char qChar = (pChar == '1' ? '0' : '1') ;
                int appearTimes = 1;
                q = p +1;
                //find first diff
                while (q < len && chars[q] == pChar) {
                    appearTimes ++;
                    q ++;
                }
                // not find
                if (q == len) {
                    return ret;
                }
                //can find nums diffs
                int diffAppearTimes = 0;
                for (; q < len && diffAppearTimes < appearTimes ; q++) {
                    if (chars[q] != qChar ) {
                        break;
                    }
                    diffAppearTimes ++;
                }
                if (diffAppearTimes == appearTimes) {
                    ret ++;
                }
            }
            return ret;
        }


        /**
         *
         * 超出时间限制
         *
         * @param s
         * @return
         */
        public int countBinarySubstrings2(String s) {
            char[] tplChars = s.toCharArray();

            int ret = 0;
            int ozRet = 0;//  01
            int zoRet = 0;//  10
            for (int i = 1; true ; i++) {
                char[] chars = new char[2 * i];
                int j = 0;
                for (j = 0; j < i; j++) {
                    chars[j] = '0';
                }
                int doubleI = 2 * i;
                for (; j < doubleI; j++) {
                    chars[j] = '1';
                }
                int frequent = countFrequent1(s, chars);
                if (frequent == 0) {
                    break;
                } else {
                    ozRet += frequent;
                }
            }

            for (int i = 1; true ; i++) {
                char[] chars = new char[2 * i];
                int j = 0;
                for (j = 0; j < i; j++) {
                    chars[j] = '1';
                }
                int doubleI = 2 * i;
                for (; j < doubleI; j++) {
                    chars[j] = '0';
                }
                int frequent = countFrequent1(s, chars);
                if (frequent == 0) {
                    break;
                } else {
                    zoRet += frequent;
                }
            }
            return ozRet + zoRet;
        }

        /**
         * 统计字符串str在字符串tplStr中出现的次数  111000  10
         *
         * @param tplStr
         * @param str
         * @return
         */
        public int countFrequent (char[] tplStr, char[] str) {
            int tplLen = tplStr.length;
            int strLen = str.length;

            if (strLen > tplLen) {
                return 0;
            }

            int retFrequent = 0;
            int start;
            int p = 0;
            int q = 0;
            while (p < tplLen) {
                start = p;
                q = 0;
                while (start < tplLen && q < strLen && tplStr[start] == str[q]) {
                    start ++;
                    q ++;
                }
                if (q == strLen) {
                    retFrequent ++;
                }
                p++;
            }
            return retFrequent;
        }

        public int countFrequent1 (String tplStr, char[] strchars) {
            int tplLen = tplStr.length();
            String str = new String(strchars);

            int strLen = str.length();

            if (strLen > tplLen) {
                return 0;
            }

            int retFrequent = 0;
            int start;
            int p = 0;
            int q = 0;
            while (p < tplLen) {
                 p = tplStr.indexOf(str, p);
                 if (p != -1) {
                     retFrequent ++;
                     p ++;
                 } else {
                     break;
                 }
            }
            return retFrequent;
        }


        /**
         *
         * 注意： 参考了扣友的思路， 找相同持续01个数，注意更新，相同个数 Min
         *
         * 执行结果：
         * 通过
         * 显示详情
         * 执行用时：
         * 8 ms
         * , 在所有 Java 提交中击败了97.66%
         * 的用户
         * 内存消耗：
         * 40.4 MB
         * , 在所有 Java 提交中击败了28.40%
         * 的用户
         *
         * @param s
         * @return
         */
        public int countBinarySubstrings3(String s) {
            int ret = 0;
            char[] chars = s.toCharArray();

            int compareChar = chars[0];
            int preCnt = 0;
            int currCnt = 0;
            for (char aChar : chars) {
                if (aChar == compareChar) {
                    currCnt ++;
                } else {
                    if (preCnt != 0) {
                        ret += Math.min(preCnt, currCnt);
                    }
                    compareChar = aChar;
                    preCnt = currCnt;
                    currCnt = 1;
                }
            }
            //少算了一次加上
            return ret + Math.min(preCnt, currCnt);
        }

    }

    public static void main(String[] args) {
        logger.info("{}", new Solution().countFrequent("000111".toCharArray(), "01".toCharArray()));//1
        logger.info("{}", new Solution().countFrequent1("000111", "01".toCharArray()));//1
        logger.info("{}", new Solution().countBinarySubstrings3("00110011"));//6   2222
        logger.info("{}", new Solution().countBinarySubstrings3("10101"));//4
        logger.info("{}", new Solution().countBinarySubstrings3(""));//4
    }
}
