  static class PloyUtils {



    /**
     * 获取ROI框选的区域点位
     * @param roi 点
     * @return
     */
    public static List<Tuple<Double, Double>> getRoiPloy(List<Double> roi) {
      List<Tuple<Double, Double>> pts = new ArrayList<>();
      for (int i = 0; i < roi.size(); i++) {
        if (i % 2 == 1) {
          pts.add(new Tuple<>(roi.get(i - 1), roi.get(i)));
        }
      }
      return pts;
    }

    /**
     * 判断点是否在多边形内
     * @param point 检测点
     * @param pts   多边形的顶点
     * @return 点在多边形内返回true, 否则返回false
     */
    public static boolean isPtInPoly(Tuple<Double, Double> point, List<Tuple<Double, Double>> pts) {
      int n = pts.size();
      //如果点位于多边形的顶点或边上，也算做点在多边形内，直接返回true
      boolean boundOrVertex = true;
      //cross points count of x
      int intersectCount = 0;
      //浮点类型计算时候与0比较时候的容差
      double precision = 2e-10;
      //neighbour bound vertices
      Tuple<Double, Double> p1, p2;
      //当前点
      Tuple<Double, Double> p = point;

      //left vertex
      p1 = pts.get(0);
      //check all rays
      for (int i = 1; i <= n; ++i) {
        if (p.equals(p1)) {
          //p is an vertex
          return boundOrVertex;
        }

        //right vertex
        p2 = pts.get(i % n);
        if (p.getItem1() < Math.min(p1.getItem1(), p2.getItem1()) || p.getItem1() > Math
            .max(p1.getItem1(), p2.getItem1())) {
          //ray is outside of our interests
          p1 = p2;
          continue;//next ray left point
        }

        if (p.getItem1() > Math.min(p1.getItem1(), p2.getItem1()) && p.getItem1() < Math
            .max(p1.getItem1(), p2.getItem1())) {
          //ray is crossing over by the algorithm (common part of)
          if (p.getItem2() <= Math.max(p1.getItem2(), p2.getItem2())) {
            //x is before of ray
            if (p1.getItem1().equals(p2.getItem1()) && p.getItem2() >= Math.min(p1.getItem2(), p2.getItem2())) {
              //overlies on a horizontal ray
              return boundOrVertex;
            }

            if (p1.getItem2().equals(p2.getItem2())) {
              //ray is vertical
              if (p1.getItem2().equals(p.getItem2())) {
                //overlies on a vertical ray
                return boundOrVertex;
              } else {//before ray
                ++intersectCount;
              }
            } else {//cross point on the left side
              double xinters = (p.getItem1() - p1.getItem1()) *
                  (p2.getItem2() - p1.getItem2()) / (p2.getItem1() - p1.getItem1()) + p1.getItem2();
              //cross point of y
              if (Math.abs(p.getItem2() - xinters) < precision) {
                //overlies on a ray
                return boundOrVertex;
              }

              if (p.getItem2() < xinters) {
                //before ray
                ++intersectCount;
              }
            }
          }
        } else {
          //special case when ray is crossing through the vertex
          if (p.getItem1().equals(p2.getItem1()) && p.getItem2() <= p2.getItem2()) {
            //p crossing over p2
            Tuple<Double, Double> p3 = pts.get((i + 1) % n);
            //next vertex
            if (p.getItem1() >= Math.min(p1.getItem1(), p3.getItem1()) && p.getItem1() <= Math
                .max(p1.getItem1(), p3.getItem1())) {
              //p.getItem1() lies between p1.getItem1() & p3.getItem1()
              ++intersectCount;
            } else {
              intersectCount += 2;
            }
          }
        }
        //next ray left point
        p1 = p2;
      }

      //偶数在多边形外
      if (intersectCount % 2 == 0) {
        return false;
      } else {
        //奇数在多边形内
        return true;
      }
    }

    /**
     * 判断是否在圆形内
     * @param p
     * @param c
     * @return
     */
    public static String distancePC(Tuple<Double, Double> p, Circle c) {//判断点与圆心之间的距离和圆半径的关系
      String s;
      double d2 = Math.hypot((p.getItem1() - c.getCc().getItem1()), (p.getItem2() - c.getCc().getItem2()));
      System.out.println("d2==" + d2);
      double r = c.getR();
      if (d2 > r) {
        s = "圆外";
      } else if (d2 < r) {
        s = "圆内";
      } else {
        s = "圆上";
      }
      return s;
    }

    /**
     * 求多边形面积
     * @param points
     * @return
     */
    public static double getArea(List<Tuple<Double, Double>> points) {
      int i, j;
      BigDecimal area = BigDecimal.ZERO;
      for (i = 0; i < points.size(); i++)
      {
        j = (i + 1) % points.size();
        area = NumberUtil.add(area, NumberUtil.mul(points.get(i).getItem1(), points.get(j).getItem2()));
        area = NumberUtil.sub(area, NumberUtil.mul(points.get(i).getItem2(), points.get(j).getItem1()));
      }
      area = NumberUtil.div(area, new BigDecimal("2"),2);
      return Math.abs(area.doubleValue());
    }


    /**
     * 圆形类
     * @author ardo
     *
     */
    @Data
    public static class Circle {
      private double r;

      private Tuple<Double, Double> cc;
    }

    private static void intersectArea() {
      Polygon p = new Polygon(new int[] {1, 1, 2, 2}, new int[] {0, 3, 1, 0}, 4);
      System.out.println(p.getBounds2D());
      Polygon p1 = new Polygon(new int[] {0, 0, 2, 2}, new int[] {0, 1, 1, 0}, 4);
      Area area = new Area(p);
      area.intersect(new Area(p1));

      System.out.println(area.isEmpty());
    }
  }


